mapper.select:
SELECT X.PROC_ID , X.MESSAGE ,X.DUEDATE, P.NAME_ PROCDEFNAME FROM( SELECT PROC_ID , MESSAGE ,DUEDATE FROM ACT_EXCEPTION UNION ALL ( SELECT J.PROCESS_INSTANCE_ID_,B.BYTES_,J.DUEDATE_ FROM ACT_RU_DEADLETTER_JOB J,ACT_GE_BYTEARRAY B WHERE J.EXCEPTION_STACK_ID_=B.ID_ )) X ,ACT_RE_PROCDEF P,ACT_HI_PROCINST O
X.PROC_ID=O.ID_ AND O.PROC_DEF_ID_=P.ID_
AND X.PROC_ID = #{procId}
AND X.DUEDATE >= #{duedate}
ORDER BY X.PROC_ID, X.DUEDATE DESC
